    print(HELP)
    while True:
        try:
            input_str = input("> ")
            if input_str == "exit":
                break
            method_to_call = getattr(Languages, 'check_word_for_language_number_' + input_str.split()[3])
            result = method_to_call(input_str.split()[1])
        except Exception:
            print("[!] Invalid Format Or maximum recursion")
